    fun calculateNoOfColumns(context: Context, columnWidthDp: Float): Int { // For example columnWidthdp=180
        val displayMetrics = context.resources.displayMetrics
        val screenWidthDp = displayMetrics.widthPixels / displayMetrics.density
        return (screenWidthDp / columnWidthDp + 0.5).toInt()
    }

    fun getBatteryDrawable(battery: Int): Int {
        return when (battery) {
            in 95..100 -> R.drawable.ic_battery_full
            in 90 until 95 -> R.drawable.ic_battery_90
            in 70 until 90 -> R.drawable.ic_battery_80
            in 60 until 70 -> R.drawable.ic_battery_60
            in 50 until 60 -> R.drawable.ic_battery_50
            in 30 until 50 -> R.drawable.ic_battery_30
            in 20 until 30 -> R.drawable.ic_battery_20
            else -> R.drawable.ic_battery_alert
        }
    }

    fun fromHtml(source: String): Spanned {
        return Html.fromHtml(source, Html.FROM_HTML_MODE_LEGACY)
    }

    fun Calendar.timeZero(): Calendar {
        val calendar = this.clone() as Calendar
        calendar.set(Calendar.HOUR_OF_DAY, 0)
        calendar.set(Calendar.MINUTE, 0)
        calendar.set(Calendar.SECOND, 0)
        calendar.set(Calendar.MILLISECOND, 0)

        return calendar
    }

    fun compareDate(d1: Long, d2: Long): Boolean {
        val c1 = Calendar.getInstance().apply { timeInMillis = d1 }.timeZero()
        val c2 = Calendar.getInstance().apply { timeInMillis = d2 }.timeZero()

        return (c1.timeInMillis == c2.timeInMillis)
    }

    fun readTextFromRaw(res: Resources, rawId: Int): String {
        val inputStream = res.openRawResource(rawId)
        val stream = InputStreamReader(inputStream, Charset.forName("UTF-8"))
        val buffer = BufferedReader(stream)
        val builder = StringBuilder()

        buffer.lineSequence().forEach { builder.append(it) }
        inputStream.close()

        return builder.toString()
    }

    fun hasProperty(characteristic: BluetoothGattCharacteristic, property: Int): Boolean = (characteristic.properties and property) == property

    fun getBluetoothGattProperty(id: Int): String {
        val result = arrayListOf<String>()
        val properties = arrayListOf(
            BluetoothGattCharacteristic.PROPERTY_BROADCAST,
            BluetoothGattCharacteristic.PROPERTY_EXTENDED_PROPS,
            BluetoothGattCharacteristic.PROPERTY_INDICATE,
            BluetoothGattCharacteristic.PROPERTY_NOTIFY,
            BluetoothGattCharacteristic.PROPERTY_READ,
            BluetoothGattCharacteristic.PROPERTY_SIGNED_WRITE,
            BluetoothGattCharacteristic.PROPERTY_WRITE,
            BluetoothGattCharacteristic.PROPERTY_WRITE_NO_RESPONSE
        )

        for (property in properties) {
            if (id and property == property) {
                result.add(when (id and property) {
                    BluetoothGattCharacteristic.PROPERTY_BROADCAST -> "PROPERTY_BROADCAST"
                    BluetoothGattCharacteristic.PROPERTY_EXTENDED_PROPS -> "PROPERTY_EXTENDED_PROPS"
                    BluetoothGattCharacteristic.PROPERTY_INDICATE -> "PROPERTY_INDICATE"
                    BluetoothGattCharacteristic.PROPERTY_NOTIFY -> "PROPERTY_NOTIFY"
                    BluetoothGattCharacteristic.PROPERTY_READ -> "PROPERTY_READ"
                    BluetoothGattCharacteristic.PROPERTY_SIGNED_WRITE -> "PROPERTY_SIGNED_WRITE"
                    BluetoothGattCharacteristic.PROPERTY_WRITE -> "PROPERTY_WRITE"
                    BluetoothGattCharacteristic.PROPERTY_WRITE_NO_RESPONSE -> "PROPERTY_WRITE_NO_RESPONSE"
                    else -> "UNKNOWN($id)"
                })
            }
        }

        return result.joinToString(",")
    }

    fun ByteArray.toInt():Int {
        val copy = copyOf()
        if (copy.size != 4) {
            throw Exception("wrong len")
        }
        copy.reverse()
        return ByteBuffer.wrap(copy).int
    }

    fun ByteArray.toDouble(): Double {
        val copy = copyOf()
        if (copy.size != 8) {
            throw Exception("wrong len: ${copy.joinToString { "${it.toInt()}," }}")
        }
        copy.reverse()
        return ByteBuffer.wrap(copy).double
    }

    fun getResourceId(resourceName: String, c: Class<*>): Int {
        return try {
            val field = c.getDeclaredField(resourceName)
            field.getInt(field)
        } catch (e: Exception) {
            e.printStackTrace()
            -1
        }
    }
